  "D.6.1.1": {
    "page_number": "128",
    "rule_number": "D.6.1.1",
    "definition": "Teams must run the tires allowed for each Operating Condition:\nOperating Condition Tires Allowed\nDry Dry ( V.4.3.1 )\nDamp Dry or Wet\nWet Wet ( V.4.3.2 )"
  },
  "D.6.1.2": {
    "page_number": "128",
    "rule_number": "D.6.1.2",
    "definition": "When the operating condition is Damp, teams may change between Dry Tires and Wet Tires:\na. Any time during the Acceleration, Skidpad, and Autocross Events\nb. Any time before starting their Endurance Event"
