  "D.11.2": {
    "page_number": "133",
    "rule_number": "D.11.2",
    "definition": "Autocross Procedure"
  },
  "D.11.2.1": {
    "page_number": "133",
    "rule_number": "D.11.2.1",
    "definition": "Each team may attempt up to four runs, using two drivers, limited to two runs for each driver"
  },
  "D.11.2.2": {
    "page_number": "133",
    "rule_number": "D.11.2.2",
    "definition": "Runs with the first driver have priority"
  },
  "D.11.2.3": {
    "page_number": "133",
    "rule_number": "D.11.2.3",
    "definition": "Each Autocross run is performed as follows:\n a. The vehicle will be staged at a specific distance behind the starting line\n b. A Green Flag or light signal will give the approval to begin the run\n c. Timing starts when the vehicle crosses the starting line\n d. Timing ends when the vehicle crosses the finish line"
  },
  "D.11.2.4": {
    "page_number": "133",
    "rule_number": "D.11.2.4",
    "definition": "Each driver may go to the front of the staging line immediately after their first run to make a\nsecond run"
  },
  "D.11.3": {
    "page_number": "133",
    "rule_number": "D.11.3",
    "definition": "Autocross Penalties"
  },
  "D.11.3.1": {
    "page_number": "133",
    "rule_number": "D.11.3.1",
    "definition": "Cones (DOO)\nTwo second penalty for each DOO (including cones after the finish line) on that run"
  },
  "D.11.3.2": {
    "page_number": "133",
    "rule_number": "D.11.3.2",
    "definition": "Off Course (OC)\n a. When an OC occurs, the driver must reenter the track at or prior to the point of exit or\n receive a 20 second penalty\n b. Penalties will not be assessed for accident avoidance or other reasons deemed sufficient\n by the track officials."
  },
  "D.11.3.3": {
    "page_number": "133",
    "rule_number": "D.11.3.3",
    "definition": "Missed Slalom\nMissing one or more gates of a given slalom will be counted as one Off Course"
