137:
{
  "D.12.11.2": {
    "page_number": "137",
    "rule_number": "D.12.11.2",
    "definition": "Passing zones have two parallel lanes – a slow lane for the vehicles that are being passed and a fast lane for vehicles that are making a pass."
  },
  "D.12.11.3": {
    "page_number": "137",
    "rule_number": "D.12.11.3",
    "definition": "When a pass is to be made:\n a. A slower leading vehicle will receive a Blue Flag\n b. The slower vehicle must move into the slow lane and decelerate.\n c. The following faster vehicle will continue in the fast lane and make the pass.\n d. The vehicle that had been passed may reenter traffic only under the control of the passing zone exit flag."
  },
  "D.12.11.4": {
    "page_number": "137",
    "rule_number": "D.12.11.4",
    "definition": "Passing rules do not apply to vehicles that are passing disabled vehicles on the course or vehicles that have spun out and are not moving. When passing a disabled or off track vehicle, slow down, drive cautiously and be aware of all the vehicles and track workers in the area."
  },
  "D.12.12": {
    "page_number": "137",
    "rule_number": "D.12.12",
    "definition": "Endurance Penalties"
  },
  "D.12.12.1": {
    "page_number": "137",
    "rule_number": "D.12.12.1",
    "definition": "Cones (DOO)\nTwo second penalty for each DOO (including cones after the finish line) on that run"
  },
  "D.12.12.2": {
    "page_number": "137",
    "rule_number": "D.12.12.2",
    "definition": "Off Course (OC)\n a. When an OC occurs, the driver must reenter the track at or prior to the point of exit or receive a 20 second penalty\n b. Penalties will not be assessed for accident avoidance or other reasons deemed sufficient by the track officials."
  },
  "D.12.12.3": {
    "page_number": "137",
    "rule_number": "D.12.12.3",
    "definition": "Missed Slalom\nMissing one or more gates of a given slalom will be counted as one Off Course"
  },
  "D.12.12.4": {
    "page_number": "137",
    "rule_number": "D.12.12.4",
    "definition": "Penalties for Moving or Post Event Violations\n a. Black Flag penalties per D.12.10, if applicable\n b. Post Event Inspection penalties per D.14.2, if applicable"
  },
  "D.12.12.5": {
    "page_number": "137",
    "rule_number": "D.12.12.5",
    "definition": "Endurance Starting (D.12.4.1)\nTwo minutes (120 seconds) penalty"
  },
  "D.12.12.6": {
    "page_number": "137",
    "rule_number": "D.12.12.6",
    "definition": "Vehicle Operation\nThe Chief Marshall/Director of Operations may end the Endurance event (DNF) a vehicle if, for any reason including driver inexperience or mechanical problems, it is too slow or being driven in a manner that demonstrates an inability to properly control."
  },
  "D.12.13": {
    "page_number": "137",
    "rule_number": "D.12.13",
    "definition": "Endurance Scoring"
  },
  "D.12.13.1": {
    "page_number": "137",
    "rule_number": "D.12.13.1",
    "definition": "Scoring Term Definitions:\n• Endurance Run Time - Total Time for both Drivers, minus the Driver Change lap, minus any Mechanical Black Flag Time, plus any Penalty time D.14.2\n• Corrected Time = Endurance Run Time + ( DOO * 2 ) + ( OC * 20 )\n• Tyour - the Corrected Time for the team\n• Tmin - the lowest Corrected Time recorded for any team\n• Tmax - 145% of Tmin"
  }
}

