  },
  "V.3.1.5": {
    "page_number": "20",
    "rule_number": "V.3.1.5",
    "definition": "All spherical rod ends and spherical bearings on the suspension and steering must be one of:\n• Mounted in double shear\n• Captured by having a screw/bolt head or washer with an outside diameter that is larger than spherical bearing housing inside diameter."
  },
  "V.3.2.1": {
    "page_number": "20",
    "rule_number": "V.3.2.1",
    "definition": "The Steering Wheel must be mechanically connected to the front wheels"
  }
}

